import React from "react";

import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import { makeStyles } from "@material-ui/core/styles";

//REDUX
import { useSelector, useDispatch } from "react-redux";
import UserMenu from "../components/userMenu";
import { CameraAlt, Edit } from "@material-ui/icons";
import Button from "../components/button";
import Input from "../components/input";

const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1, 1, 0, 0),
  },
  icon: {
    borderRadius: "50%",
    width: 16,
    height: 16,
    boxShadow:
      "inset 0 0 0 1px rgba(16,22,26,.2), inset 0 -1px 0 rgba(16,22,26,.1)",
    backgroundColor: "#f5f8fa",
    backgroundImage:
      "linear-gradient(180deg,hsla(0,0%,100%,.8),hsla(0,0%,100%,0))",
    "$root.Mui-focusVisible &": {
      outline: "2px auto rgba(19,124,189,.6)",
      outlineOffset: 2,
    },
    "input:hover ~ &": {
      backgroundColor: "#ebf1f5",
    },
    "input:disabled ~ &": {
      boxShadow: "none",
      background: "rgba(206,217,224,.5)",
    },
  },
  checkedIcon: {
    backgroundColor: "#137cbd",
    backgroundImage:
      "linear-gradient(180deg,hsla(0,0%,100%,.1),hsla(0,0%,100%,0))",
    "&:before": {
      display: "block",
      width: 16,
      height: 16,
      backgroundImage: "radial-gradient(#fff,#fff 28%,transparent 32%)",
      content: '""',
    },
    "input:hover ~ &": {
      backgroundColor: "#106ba3",
    },
  },
}));

function StyledRadio(props) {
  const classes = useStyles();

  return (
    <Radio
      className={classes.root}
      disableRipple
      color="default"
      checkedIcon={<span className={clsx(classes.icon, classes.checkedIcon)} />}
      icon={<span className={classes.icon} />}
      {...props}
    />
  );
}

export default function Welcome() {
  const state = useSelector((state) => state.AuthProvider);
  const [showEdit, setShowEdit] = React.useState(false);
  //   const dispatch = useDispatch();
  const classes = useStyles();
  const [value, setValue] = React.useState("");
  const [error, setError] = React.useState(false);

  const handleRadioChange = (event) => {
    setValue(event.target.value);
    // setHelperText(" ");
    setError(false);
  };

  return (
    <div
      className="py-4 px-24"
      // style={{ backgroundImage: `url(${login})` }}
    >
      <div className="flex flex-row gap-4">
        <UserMenu />
        <div className="flex-1 bg-white shadow-lg rounded py-2 px-4 text-gray-600">
          <div className="flex justify-start gap-4 w-1/2">
            <div className="underline text-lg">Personal Information</div>
            <div className="flex">
              <div className="my-auto" className="cursor-pointer">
                <Edit fontSize={"small"} />
              </div>
            </div>
          </div>
          <div>
            <div>Photo</div>
            <div
              className="relative w-36 h-36 rounded-full"
              onMouseOver={() => setShowEdit(true)}
              onMouseLeave={() => setShowEdit(false)}
            >
              <img
                src="https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/profile-pic-male_4811a1.svg"
                alt="dp"
                className="shadow-lg w-36 h-36 rounded-full hover:opacity-50"
              />
              {showEdit && (
                <div
                  className="w-36 h-36 absolute bg-transparent top-0 left-0 rounded-full flex flex-col justify-center cursor-pointer"
                  style={{ background: "rgba(0,0,0,0.5)" }}
                  onClick={() => console.log("Handle")}
                >
                  <div className="my-auto text-center text-white">
                    <div>
                      <CameraAlt fontSize={"small"} />
                    </div>
                    <div>Edit</div>
                  </div>
                </div>
              )}
            </div>

            <div className="my-4 w-1/2">
              <div className="mb-5">
                Name
                <Input
                  placeholder="Name"
                  className="mb-10px"
                  name="name"
                  value={state.user.customerName}
                  // onChange={onChange}
                />
              </div>

              <div className="mb-5">
                Phone Number
                <Input
                  placeholder="Phone Number"
                  className="mb-10px"
                  name="phone"
                  value={state.user.mobileNo}
                  // onChange={onChange}
                />
              </div>

              <div className="mb-5">
                Gender
                <RadioGroup
                  aria-label="gender"
                  name="gender"
                  value="male"
                  onChange={handleRadioChange}
                  classes={{ root: classes.radio }}
                >
                  <div className="flex">
                    <FormControlLabel
                      value="male"
                      control={<Radio />}
                      label="Male"
                    />
                    <FormControlLabel
                      value="female"
                      control={<Radio />}
                      label="Female"
                    />
                    <FormControlLabel
                      value="other"
                      control={<Radio />}
                      label="Other"
                    />
                  </div>
                </RadioGroup>
              </div>

              <div className="mb-5">
                Email
                <Input
                  placeholder="Email"
                  className="mb-10px"
                  name="email"
                  value={state.user.emailId}
                  // onChange={onChange}
                />
              </div>
              {/* EMAIL
              Mobile
              NAME
               */}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
