import React from "react";

import { withStyles } from "@material-ui/core/styles";
import { green } from "@material-ui/core/colors";
import Radio from "@material-ui/core/Radio";
//REDUX
import { useSelector, useDispatch } from "react-redux";
import UserMenu from "../components/userMenu";
import { Camera, CameraAlt, Edit } from "@material-ui/icons";
import Button from "../components/button";
import Input from "../components/input";

const GreenRadio = withStyles({
  root: {
    color: green[400],
    "&$checked": {
      color: green[600],
    },
  },
  checked: {},
})((props) => <Radio color="default" {...props} />);

export default function Welcome() {
  const state = useSelector((state) => state.AuthProvider);
  const [showEdit, setShowEdit] = React.useState(false);
  //   const dispatch = useDispatch();
  const [selectedValue, setSelectedValue] = React.useState("a");

  const handleChange = (event) => {
    setSelectedValue(event.target.value);
  };

  return (
    <div
      className="py-4 px-24"
      // style={{ backgroundImage: `url(${login})` }}
    >
      <div className="flex flex-row gap-4">
        <UserMenu />
        <div className="flex-1 bg-white shadow-lg rounded py-2 px-4 text-gray-600">
          <div className="flex justify-start gap-4 w-1/2">
            <div className="underline text-lg">Personal Information</div>
            <div className="flex">
              <div className="my-auto" className="cursor-pointer">
                <Edit fontSize={"small"} />
              </div>
            </div>
          </div>
          <div>
            <div>Photo</div>
            <div
              className="relative w-36 h-36 rounded-full"
              onMouseOver={() => setShowEdit(true)}
              onMouseLeave={() => setShowEdit(false)}
            >
              <img
                src="https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/profile-pic-male_4811a1.svg"
                alt="dp"
                className="shadow-lg w-36 h-36 rounded-full hover:opacity-50"
              />
              {showEdit && (
                <div
                  className="w-36 h-36 absolute bg-transparent top-0 left-0 rounded-full flex flex-col justify-center cursor-pointer"
                  style={{ background: "rgba(0,0,0,0.5)" }}
                  onClick={() => console.log("Handle")}
                >
                  <div className="my-auto text-center text-white">
                    <div>
                      <CameraAlt fontSize={"small"} />
                    </div>
                    <div>Edit</div>
                  </div>
                </div>
              )}
            </div>

            <div className="my-4 w-1/2">
              <div className="mb-5">
                Name
                <Input
                  placeholder="Name"
                  className="mb-10px"
                  name="name"
                  value={state.user.customerName}
                  // onChange={onChange}
                />
              </div>

              <div className="mb-5">
                Phone Number
                <Input
                  placeholder="Phone Number"
                  className="mb-10px"
                  name="phone"
                  value={state.user.mobileNo}
                  // onChange={onChange}
                />
              </div>

              <div className="mb-5">
                Gender
                <GreenRadio
                  checked={selectedValue === "c"}
                  onChange={handleChange}
                  value="c"
                  name="radio-button-demo"
                  inputProps={{ "aria-label": "C" }}
                />
                Male
                <GreenRadio
                  checked={selectedValue === "c"}
                  onChange={handleChange}
                  value="c"
                  name="radio-button-demo"
                  inputProps={{ "aria-label": "C" }}
                />
                FEmale
              </div>

              <div className="mb-5">
                Email
                <Input
                  placeholder="Email"
                  className="mb-10px"
                  name="email"
                  value={state.user.emailId}
                  // onChange={onChange}
                />
              </div>
              {/* EMAIL
              Mobile
              NAME
               */}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
