import axios from "axios";

import * as c from "./constant";

//CUSTOMER API's
export async function login_customer(data) {
  try {
    let url = c.LOGIN_CUSTOMER;
    let res = await axios.post(url, data);
    return res;
  } catch (e) {
    return e.response;
  }
}

export async function register_customer(data) {
  try {
    let url = c.REGISTER_CUSTOMER;
    let res = await axios.post(url, data);
    return res;
  } catch (e) {
    return e.response;
  }
}

export async function customer_email_verification(data) {
  try {
    let url = c.CUSTOMER_EMAIL_VERIFICATION;
    let res = await axios.post(url, data);
    return res;
  } catch (e) {
    return e.response;
  }
}

export async function forgot_password(data) {
  try {
    let url = c.FORGOT_PASSWORD;
    let res = await axios.post(url, data);
    return res;
  } catch (e) {
    return e.response;
  }
}

//CATEGORY API's

export async function category() {
  try {
    let url = c.CATEGORY;
    let res = await axios.get(url);
    return res;
  } catch (e) {
    return e.response;
  }
}
