import React from "react";
// FaTshirt;
import { FaTshirt } from "react-icons/fa";
import { HiChevronRight } from "react-icons/hi";

//ICONS
import {
  IoHomeOutline,
  IoShirtOutline,
  IoCameraOutline,
  IoBedOutline,
  IoGameControllerOutline,
  IoGiftOutline,
  IoPhonePortraitOutline,
} from "react-icons/io5";
import { ImDisplay } from "react-icons/im";
import { RiVipDiamondLine } from "react-icons/ri";
import { CgBowl } from "react-icons/cg";
import { GiHealthPotion } from "react-icons/gi";

import Carousel from "react-multi-carousel";
import Banner1 from "../assets/images/main.png";
import Banner2 from "../assets/images/main2.png";
import Banner3 from "../assets/images/main3.png";
import Banner4 from "../assets/images/main4.png";

import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";

export default function Banner() {
  // const handleMouseOver = () => {
  //   console.log("Hi");
  // };

  const [show, setShow] = React.useState(false);

  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleMouseOver = (event) => {
    console.log(event);
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleOver = () => {
    setShow(true);
  };

  const handleOut = () => {
    setShow(false);
  };

  return (
    <div className="flex flex-row gap-4 h-auto mx-24 my-2">
      <div className="w-80">
        <div className="h-auto divide divide-y text-gray-600 bg-white rounded shadow-lg">
