import React from "react";
import Offer from "../assets/icons/offer.png";
import Logo from "../assets/images/logo2.png";

import {
  KeyboardArrowDown,
  PersonOutline,
  ShoppingCart,
} from "@material-ui/icons";
import Button from "./button";
import { Link } from "react-router-dom";

//REDUX
import { useSelector } from "react-redux";

export default function MegaMenu() {
  const state = useSelector((state) => state.AuthProvider);

  let menuData = [];
  state.category &&
    state.category.map((item) => {
      return menuData.push(item);
    });

  const handleMouseOver = (mega, id) => {
    // setMouseOver(true);
    if (mega === true) {
    }
  };

  const handleMouseOut = () => {};

  return (
    <div>
      {/* TOP SECTION */}
      <div className="w-full py-4 px-24 flex flex-row justify-between">
        <div className="uppercase text-sm text-gray-500 my-auto ">
          <img src={Logo} alt="logo" />
        </div>
        <div className=" w-1/2">
          <div className="pt-1 mt-1 relative mx-auto text-gray-600 my-auto">
            <input
              className="border-2 border-dodger-blue-600 focus:border-dodger-blue-500 focus:border-4 w-full bg-white h-9 px-5 pr-12 rounded-lg text-sm focus:outline-none my-auto"
              type="search"
              name="search"
              placeholder="Search"
            />
            <button type="submit" className="absolute right-0 -top-1 mt-5 mr-4">
              <svg
                className="text-gray-600 h-4 w-4 fill-current"
                // xmlns="http://www.w3.org/2000/svg"
                // xmlns:xlink="http://www.w3.org/1999/xlink"
                version="1.1"
                id="Capa_1"
                x="0px"
                y="0px"
                viewBox="0 0 56.966 56.966"
                // style={{enableBackforund: "new 0 0 56.966 56.966"}}
                // xml:space="preserve"
                width="512px"
                height="512px"
              >
                <path d="M55.146,51.887L41.588,37.786c3.486-4.144,5.396-9.358,5.396-14.786c0-12.682-10.318-23-23-23s-23,10.318-23,23  s10.318,23,23,23c4.761,0,9.298-1.436,13.177-4.162l13.661,14.208c0.571,0.593,1.339,0.92,2.162,0.92  c0.779,0,1.518-0.297,2.079-0.837C56.255,54.982,56.293,53.08,55.146,51.887z M23.984,6c9.374,0,17,7.626,17,17s-7.626,17-17,17  s-17-7.626-17-17S14.61,6,23.984,6z" />
              </svg>
            </button>
          </div>
        </div>
        <div className="flex flex-row gap-4 text-sm text-gray-500 my-auto ">
          {/* <div className="my-auto">USD</div> */}
          <div>
            {state.isAuthenticated ? (
              <Link to="/welcome">
                <Button
                  size={"small"}
                  className="w-full"
                  variant={"primary"}
                  //   onClick={handleSubmit}
                  //   loading={loading}
                >
                  <PersonOutline />
                  {state.user.customerName}
                </Button>
              </Link>
            ) : (
              <Link to="/login">
                <Button
                  size={"small"}
                  className="w-full"
                  variant={"primary"}
                  //   onClick={handleSubmit}
                  //   loading={loading}
                >
                  Login/ Signup
                </Button>
              </Link>
            )}
          </div>
          <button className="my-auto flex flex-row gap-1 px-4 py-2 rounded hover:bg-gray-200">
            Return / Orders
          </button>
          <button className="my-auto flex flex-row gap-1 px-4 py-2 rounded hover:bg-gray-200">
            <ShoppingCart style={{ fontSize: "20px" }} />
            Cart
          </button>
        </div>
      </div>

      {/* MENU */}
      <div className="px-24 py-2 bg-white flex flex-row gap-4 justify-between shadow-xl">
        {menuData &&
          menuData.map((item) => {
            return (
              <div
                key={item.id}
                className="flex flex-col cursor-default font-semibold menuDiv "
                // onMouseOver={handleMouseOver}
                // onMouseOut={handleMouseOut}
                onMouseEnter={() => handleMouseOver(item.mega, item.id)}
                onMouseLeave={handleMouseOut}
              >
                <div
                  className="w-16 h-16 bg-contain bg-no-repeat bg-center mx-auto"
                  style={{ backgroundImage: `url(${Offer})` }}
                ></div>
                <div className="text-sm cursor-default flex flex-row gap-1 justify-center">
                  <div id="menuTitle" className="font-medium">
                    {item.catName}
                  </div>
                  {item.subcategory.length ? (
                    <div
                      id="arrow"
                      className="transition duration-100 ease-in-out"
                    >
                      <KeyboardArrowDown style={{ fontSize: "18px" }} />
                    </div>
                  ) : null}
                </div>
              </div>
            );
          })}
      </div>
    </div>
  );
}
