import React from "react";
import Button from "../components/button";
import login from "../assets/images/login.png";

import { Link } from "react-router-dom";

//REDUX
import { useSelector } from "react-redux";

export default function Welcome() {
  const state = useSelector((state) => state.AuthProvider.user);

  //   const dispatch = useDispatch();

  return (
    <div
      className="min-h-screen flex flex-col justify-center sm:py-12 bg-cover bg-center bg-no-repeat px-24"
      // style={{ backgroundImage: `url(${login})` }}
    >
      <div className="text-center text-black font-bold capitalize top-24 w-full left-24">
        <div className="flex flex-row gap-4">
          <div className="w-80 h-96">
            <div className="h-20 bg-white shadow-lg rounded"></div>
          </div>
          <div className="flex-1 border">Content</div>
        </div>
        <div className="text-7xl">Welcome {state.customerName},</div>
        <div className="text-7xl">We'll launch soon!</div>
        <div className="w-40 mx-auto my-2">
          <Link to="/">
            <Button
              size={"big"}
              className="w-full relative"
              variant={"primary"}
              id="loginBtn"
            >
              Back to home
            </Button>
          </Link>
        </div>
      </div>
    </div>
  );
}
