import React from "react";

// ICONS
import { FaPowerOff } from "react-icons/fa";
import { BsFillPersonFill, BsFillFolderFill, BsPower } from "react-icons/bs";
import { HiChevronRight } from "react-icons/hi";
import { AiFillEdit } from "react-icons/ai";

import { useHistory } from "react-router-dom";
//REDUX
import { useSelector, useDispatch } from "react-redux";
import UserMenu from "../components/userMenu";
import { Edit } from "@material-ui/icons";

export default function Welcome() {
  const state = useSelector((state) => state.AuthProvider);
  const userData = state.user;
  const history = useHistory();
  const dispatch = useDispatch();

  //   const dispatch = useDispatch();

  return (
    <div
      className="py-4 px-24"
      // style={{ backgroundImage: `url(${login})` }}
    >
      <div className="flex flex-row gap-4">
        <UserMenu />
        <div className="flex-1 bg-white shadow-lg rounded py-2 px-4 text-gray-600">
          <div className="flex justify-between">
            <div className="underline">Personal Information</div>
            <div className="flex">
              <div className="my-auto" className="cursor-pointer">
                <Edit fontSize={"small"} />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
