import React from "react";
import Button from "../components/button";
import login from "../assets/images/login.png";

import { Link } from "react-router-dom";

import { BsInboxFill } from "react-icons/bs";
//REDUX
import { useSelector } from "react-redux";

export default function Welcome() {
  const state = useSelector((state) => state.AuthProvider);
  const userData = state.user;

  //   const dispatch = useDispatch();

  return (
    <div
      className="py-4 px-24"
      // style={{ backgroundImage: `url(${login})` }}
    >
      <div className="flex flex-row gap-4">
        <div className="w-80 h-96 pr-2">
          {/* USER IMAGE */}
          <div className="bg-white shadow-lg rounded p-3 flex gap-4 mb-4">
            <div className="rounded-full w-12 h-12">
              <img
                src="https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/profile-pic-male_4811a1.svg"
                alt="dp"
              />
            </div>
            <div className="">
              <div className="text-sm">Hello,</div>
              <div className="font-semibold">{userData?.customerName}</div>
            </div>
          </div>

          <div className="bg-white shadow-lg rounded p-3 mb-4">
            <div className="flex justify-between">
              <div>
                <BsInboxFill />
              </div>
              <div className="uppercase text-xl"> My orders</div>
            </div>
            <div>My orders</div>
            <div>My orders</div>
          </div>
        </div>
        <div className="flex-1 border">Content</div>
      </div>
      <div className="text-center text-black font-bold capitalize w-full">
        <div className="text-7xl">Welcome {state.customerName},</div>
        <div className="text-7xl">We'll launch soon!</div>
        <div className="w-40 mx-auto my-2">
          <Link to="/">
            <Button
              size={"big"}
              className="w-full relative"
              variant={"primary"}
              id="loginBtn"
            >
              Back to home
            </Button>
          </Link>
        </div>
      </div>
    </div>
  );
}
