import React, { useEffect, useState } from "react";
import {
  TextBoxCommonBase,
  InputBase,
  TextBoxDisable,
  TextBoxEnable,
} from "./../utils/theme";

export const defaultProps = {
  disabled: false,
  readOnly: false,
  className: "",
  placeholder: "",
  initialValue: "",
};

const Input = ({
  className,
  id,
  value,
  name,
  initialValue,
  disabled,
  readOnly,
  placeholder,
  onChange,
  onFocus,
  onBlur,
  ...props
}) => {
  const [initValue, setInitValue] = useState(initialValue);

  const changeHandler = (event) => {
    if (disabled || readOnly) return;
    setInitValue(event.target.value);
    onChange && onChange(event);
  };

  const focusHandler = (e) => {
    onFocus && onFocus(e);
  };

  const blurHandler = (e) => {
    onBlur && onBlur(e);
  };

  useEffect(() => {
    if (value === undefined) return;
    setInitValue(value);
  }, [value]);

  const classNames =
    InputBase +
    " " +
    TextBoxCommonBase +
    " " +
    (disabled === true ? TextBoxDisable : TextBoxEnable) +
    " " +
    className;

  return (
    <React.Fragment>
      <label htmlFor={id} className="sr-only">
        {name}
      </label>
      <input
        type="text"
        placeholder={placeholder}
        className={classNames}
        id={name}
        value={initValue}
        disabled={disabled}
        readOnly={readOnly}
        onChange={changeHandler}
        onFocus={focusHandler}
        onBlur={blurHandler}
        autoComplete="off"
        name={name}
        {...props}
      />
    </React.Fragment>
  );
};

Input.defaultProps = defaultProps;

export default Input;

