import React from "react";
// FaTshirt;
import { FaTshirt } from "react-icons/fa";

import Carousel from "react-multi-carousel";
import Banner1 from "../assets/images/main1.png";
import Banner2 from "../assets/images/main2.png";
export default function Banner() {
  return (
    <div className="flex flex-row gap-2 h-auto mx-24">
      <div className="w-80">
        <div className="h-96 divide divide-y">
          <div className="py-1 flex flex-row">
            <div className="my-auto">
              <FaTshirt className="bg-red-200" />
            </div>
            Fashion
          </div>
          <div className="py-1">Fashion</div>
          <div className="py-1">Fashion</div>
          <div className="py-1">Fashion</div>
          <div className="py-1">Fashion</div>
          <div className="py-1">Fashion</div>
          <div className="py-1">Fashion</div>
        </div>
      </div>
      <div className="bg-green-800" style={{ width: "calc(100% - 20rem" }}>
        <Carousel
          arrows={false}
          additionalTransfrom={0}
          ssr={true}
          showDots={false}
          slidesToSlide={1}
          infinite
          autoPlay={true}
          centerMode={false}
          className=""
          containerClass="relative"
          dotListClass=""
          draggable
          // customButtonGroup={<ButtonGroup2 />}
          renderButtonGroupOutside={true}
          focusOnSelect={false}
          itemClass=""
          keyBoardControl
          minimumTouchDrag={80}
          responsive={{
            desktop: {
              breakpoint: {
                max: 3000,
                min: 1024,
              },
              items: 1,
              partialVisibilityGutter: 40,
            },
            mobile: {
              breakpoint: {
                max: 464,
                min: 0,
              },
              items: 1,
              partialVisibilityGutter: 30,
            },
            tablet: {
              breakpoint: {
                max: 1024,
                min: 464,
              },
              items: 1,
              partialVisibilityGutter: 30,
            },
          }}
        >
          <div
            className="w-full h-96 bg-center bg-cover bg-no-repeat"
            style={{ backgroundImage: `url(${Banner1})` }}
          ></div>
          <div
            className="w-full h-96 bg-center bg-cover bg-no-repeat"
            style={{ backgroundImage: `url(${Banner2})` }}
          ></div>
        </Carousel>
      </div>
    </div>
  );
}
