import React from "react";
// FaTshirt;
import { FaTshirt } from "react-icons/fa";
import { HiChevronRight } from "react-icons/hi";

//ICONS
import {
  IoHomeOutline,
  IoShirtOutline,
  IoCameraOutline,
  IoBedOutline,
  IoGameControllerOutline,
  IoGiftOutline,
  IoPhonePortraitOutline,
} from "react-icons/io5";
import { ImDisplay } from "react-icons/im";
import { RiVipDiamondLine } from "react-icons/ri";
import { CgBowl } from "react-icons/cg";
import { GiHealthPotion } from "react-icons/gi";

import Carousel from "react-multi-carousel";
import Banner1 from "../assets/images/main.png";
import Banner2 from "../assets/images/main2.png";
import Banner3 from "../assets/images/main3.png";
import Banner4 from "../assets/images/main4.png";

import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";

export default function Banner() {
  // const handleMouseOver = () => {
  //   console.log("Hi");
  // };

  const [show, setShow] = React.useState(false);

  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleMouseOver = (event) => {
    console.log(event);
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleOver = () => {
    setShow(true);
  };

  const handleOut = () => {
    setShow(false);
  };

  return (
    <div className="flex flex-row gap-4 h-auto mx-24 my-2">
      <div className="w-80">
        <div className="h-auto divide divide-y text-gray-600 bg-white rounded shadow-lg">
          <div
            className="flex flex-row justify-between px-2 cursor-default hover:bg-gray-300 relative"
            // onMouseOver={handleMouseOver}
            // onBlur={handleClose}
            onMouseOver={handleOver}
            // onMouseOut={handleOut}
          >
            <div className="py-2 flex flex-row gap-2">
              <div className="my-auto">
                <IoShirtOutline />
              </div>
              <div>Fashion</div>
            </div>
            <div className="my-auto">
              <HiChevronRight />
            </div>
            {show && (
              <div
                className="absolute bg-white px-2 divide divide-y  -right-40 rounded shadow-xl z-50 w-48 flex flex-col"
                onMouseOver={handleOver}
              >
                <div className="flex flex-row justify-between">
                  <div className="py-2 flex flex-row gap-2">
                    <div className="my-auto">
                      <RiVipDiamondLine />
                    </div>
                    <div>Accessories</div>
                  </div>
                  <div className="my-auto">
                    <HiChevronRight />
                  </div>
                </div>

                <div className="flex flex-row justify-between">
                  <div className="py-2 flex flex-row gap-2">
                    <div className="my-auto">
                      <RiVipDiamondLine />
                    </div>
                    <div>Accessories</div>
                  </div>
                  <div className="my-auto">
                    <HiChevronRight />
                  </div>
                </div>

                <div className="flex flex-row justify-between">
                  <div className="py-2 flex flex-row gap-2">
                    <div className="my-auto">
                      <RiVipDiamondLine />
                    </div>
                    <div>Accessories</div>
                  </div>
                  <div className="my-auto">
                    <HiChevronRight />
                  </div>
                </div>
              </div>
            )}
          </div>

          <div className="flex flex-row justify-between px-2 cursor-default hover:bg-gray-300">
            <div className="py-2 flex flex-row gap-2">
              <div className="my-auto">
                <IoHomeOutline />
              </div>
              <div>Home and Garden</div>
            </div>
            <div className="my-auto">
              <HiChevronRight />
            </div>
          </div>

          <div className="flex flex-row justify-between px-2 cursor-default hover:bg-gray-300">
            <div className="py-2 flex flex-row gap-2">
              <div className="my-auto">
                <ImDisplay />
              </div>
              <div>Electronics</div>
            </div>
            <div className="my-auto">
              <HiChevronRight />
            </div>
          </div>

          <div className="flex flex-row justify-between px-2 cursor-default hover:bg-gray-300">
            <div className="py-2 flex flex-row gap-2">
              <div className="my-auto">
                <IoBedOutline />
              </div>
              <div>Furniture</div>
            </div>
            <div className="my-auto">
              <HiChevronRight />
            </div>
          </div>

          <div className="flex flex-row justify-between px-2 cursor-default hover:bg-gray-300">
            <div className="py-2 flex flex-row gap-2">
              <div className="my-auto">
                <GiHealthPotion />
              </div>
              Health & Beauty
            </div>
            <div className="my-auto">
              <HiChevronRight />
            </div>
          </div>

          <div className="flex flex-row justify-between px-2 cursor-default hover:bg-gray-300">
            <div className="py-2 flex flex-row gap-2">
              <div className="my-auto">
                <IoGiftOutline />
              </div>
              <div>Gift Ideas</div>
            </div>
            <div className="my-auto">
              <HiChevronRight />
            </div>
          </div>

          <div className="flex flex-row justify-between px-2 cursor-default hover:bg-gray-300">
            <div className="py-2 flex flex-row gap-2">
              <div className="my-auto">
                <IoGameControllerOutline />
              </div>
              <div>Toy & Games</div>
            </div>
            <div className="my-auto">
              <HiChevronRight />
            </div>
          </div>

          <div className="flex flex-row justify-between px-2 cursor-default hover:bg-gray-300">
            <div className="py-2 flex flex-row gap-2">
              <div className="my-auto">
                <CgBowl />
              </div>
              <div>Cooking</div>
            </div>
            <div className="my-auto">
              <HiChevronRight />
            </div>
          </div>

          <div className="flex flex-row justify-between px-2 cursor-default hover:bg-gray-300">
            <div className="py-2 flex flex-row gap-2">
              <div className="my-auto">
                <IoPhonePortraitOutline />
              </div>
              <div>Smart Phones</div>
            </div>
            <div className="my-auto">
              <HiChevronRight />
            </div>
          </div>

          <div className="flex flex-row justify-between px-2 cursor-default hover:bg-gray-300">
            <div className="py-2 flex flex-row gap-2">
              <div className="my-auto">
                <IoCameraOutline />
              </div>
              <div>Camera</div>
            </div>
            <div className="my-auto">
              <HiChevronRight />
            </div>
          </div>

          <div className="flex flex-row justify-between px-2 cursor-default hover:bg-gray-300">
            <div className="py-2 flex flex-row gap-2">
              <div className="my-auto">
                <RiVipDiamondLine />
              </div>
              <div>Accessories</div>
            </div>
            <div className="my-auto">
              <HiChevronRight />
            </div>
          </div>
        </div>

        {/* <Menu
          id="simple-menu"
          anchorEl={anchorEl}
          keepMounted
          open={Boolean(anchorEl)}
          onClose={handleClose}
          anchorOrigin={{ vertical: "bottom", horizontal: "right" }}
          transformOrigin={{ vertical: "top", horizontal: "left" }}
        >
          <MenuItem>
            {" "}
            <span className="text-gray-600">Profile</span>
          </MenuItem>
          <MenuItem>
            {" "}
            <span className="text-gray-600">My account</span>
          </MenuItem>
          <MenuItem>
            {" "}
            <span className="text-gray-600">Logout</span>
          </MenuItem>
        </Menu> */}
      </div>
      <div
        className="bg-green-800 shadow-lg rounded overflow-hidden"
        style={{ width: "calc(100% - 20rem" }}
      >
        <Carousel
          arrows={false}
          additionalTransfrom={0}
          ssr={true}
          showDots={false}
          slidesToSlide={1}
          infinite
          autoPlay={true}
          centerMode={false}
          className=""
          containerClass="relative"
          dotListClass=""
          draggable
          // customButtonGroup={<ButtonGroup2 />}
          renderButtonGroupOutside={true}
          focusOnSelect={false}
          itemClass=""
          keyBoardControl
          minimumTouchDrag={80}
          responsive={{
            desktop: {
              breakpoint: {
                max: 3000,
                min: 1024,
              },
              items: 1,
              partialVisibilityGutter: 40,
            },
            mobile: {
              breakpoint: {
                max: 464,
                min: 0,
              },
              items: 1,
              partialVisibilityGutter: 30,
            },
            tablet: {
              breakpoint: {
                max: 1024,
                min: 464,
              },
              items: 1,
              partialVisibilityGutter: 30,
            },
          }}
        >
          <div
            className="w-full h-96 bg-center bg-cover bg-no-repeat"
            style={{ backgroundImage: `url(${Banner4})`, height: "450px" }}
          ></div>
          <div
            className="w-full bg-center bg-cover bg-no-repeat"
            style={{ backgroundImage: `url(${Banner1})`, height: "450px" }}
          ></div>
          <div
            className="w-full h-96 bg-center bg-cover bg-no-repeat"
            style={{ backgroundImage: `url(${Banner2})`, height: "450px" }}
          ></div>
          <div
            className="w-full h-96 bg-center bg-cover bg-no-repeat"
            style={{ backgroundImage: `url(${Banner3})`, height: "450px" }}
          ></div>
        </Carousel>
      </div>
    </div>
  );
}
