import React from "react";

import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import { makeStyles } from "@material-ui/core/styles";

//REDUX
import { useSelector, useDispatch } from "react-redux";
import UserMenu from "../../components/userMenu";
import Button from "../../components/button";
import Input from "../../components/input";
import { BsThreeDotsVertical } from "react-icons/bs";

const address = [
  {
    id: "1",
    type: "HOME",
    name: "Vaibhav Sinha",
    phone: "6205630243",
    pin: "828104",
    address:
      "S/o Mr Dilip Kumar Sinha Shyam Nagar, Bhuli Dhanbad, Near Aadarsh Vidya Mandir School",
    city: "Dhanbad",
    state: "Jharkhand",
  },
  {
    id: "2",
    type: "office",
    name: "Vaibhav Sinha",
    phone: "6205630243",
    pin: "828104",
    address:
      "S/o Mr Dilip Kumar Sinha Shyam Nagar, Bhuli Dhanbad, Near Aadarsh Vidya Mandir School",
    city: "Dhanbad",
    state: "Jharkhand",
  },
  {
    id: "3",
    type: "office",
    name: "Vaibhav Sinha",
    phone: "6205630243",
    pin: "828104",
    address:
      "S/o Mr Dilip Kumar Sinha Shyam Nagar, Bhuli Dhanbad, Near Aadarsh Vidya Mandir School",
    city: "Dhanbad",
    state: "Jharkhand",
  },
  {
    id: "4",
    type: "office",
    name: "Vaibhav Sinha",
    phone: "6205630243",
    pin: "828104",
    address:
      "S/o Mr Dilip Kumar Sinha Shyam Nagar, Bhuli Dhanbad, Near Aadarsh Vidya Mandir School",
    city: "Dhanbad",
    state: "Jharkhand",
  },
  {
    id: "5",
    type: "office",
    name: "Vaibhav Sinha",
    phone: "6205630243",
    pin: "828104",
    address:
      "S/o Mr Dilip Kumar Sinha Shyam Nagar, Bhuli Dhanbad, Near Aadarsh Vidya Mandir School",
    city: "Dhanbad",
    state: "Jharkhand",
  },
];

export default function Address() {
  const state = useSelector((state) => state.AuthProvider);

  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <div
      className="py-4 px-24"
      // style={{ backgroundImage: `url(${login})` }}
    >
      <div className="flex flex-row gap-4">
        <UserMenu active={"address"} />
        <div className="flex-1 bg-white shadow-lg rounded py-2 px-4 text-gray-600">
          <div className="flex justify-between gap-4 w-1/2 py-3">
            <div className="underline text-lg">Manage Address</div>
          </div>
          <div className="mb-4">
            <Button
              size={"small"}
              className="w-full my-auto text-xs"
              variant={"primary"}
            >
              Add A New Address
            </Button>
            <div
              className="h-96 w-full my-3 p-3"
              style={{ background: "rgba(200, 235, 228, 0.3)" }}
            >
              <span className="text-sm font-semibold uppercase text-gray-500">
                Add a new address
              </span>
              <div className="my-2">
                <div className="flex gap-5">
                  <div className="mb-5 flex-1">
                    {/* Name */}
                    <Input
                      placeholder="Name"
                      className={`mb-10px `}
                      name="name"
                      // value={state.user.customerName}
                      // onChange={onChange}
                    />
                  </div>
                  <div className="mb-5 flex-1">
                    {/* Phone Number */}
                    <Input
                      placeholder="10 digit Mobile Number"
                      className={`mb-10px `}
                      name="name"
                      // value={state.user.customerName}
                      // onChange={onChange}
                    />
                  </div>
                </div>

                <div className="flex gap-5">
                  <div className="mb-5 flex-1">
                    {/* Name */}
                    <Input
                      placeholder="Pin Code"
                      className={`mb-10px `}
                      name="name"
                      // value={state.user.customerName}
                      // onChange={onChange}
                    />
                  </div>
                  <div className="mb-5 flex-1">
                    {/* Phone Number */}
                    <Input
                      placeholder="Locality"
                      className={`mb-10px `}
                      name="name"
                      // value={state.user.customerName}
                      // onChange={onChange}
                    />
                  </div>
                </div>
                <div className="flex gap-5">
                  <div className="">
                    <textarea type="area" rows="5" className="w-full" />
                  </div>
                </div>
                <div className="flex gap-5">
                  <div className="mb-5 flex-1">
                    {/* Name */}
                    <Input
                      placeholder="City"
                      className={`mb-10px `}
                      name="name"
                      // value={state.user.customerName}
                      // onChange={onChange}
                    />
                  </div>
                  <div className="mb-5 flex-1">
                    {/* Phone Number */}
                    <Input
                      placeholder="State"
                      className={`mb-10px `}
                      name="name"
                      // value={state.user.customerName}
                      // onChange={onChange}
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>

          {address.map((item) => {
            const { id, type, name, phone, pin, address, city, state } = item;
            return (
              <div
                className="h-auto border w-full p-3 overflow-hidden bg-white -mt-px"
                key={id}
              >
                <div className="flex justify-between pb-2">
                  <div className="py-1 px-2 bg-gray-200 text-xs uppercase rounded text-gray-700">
                    {type}
                  </div>
                  <div
                    className=""
                    onClick={handleClick}
                    aria-controls="simple-menu"
                    aria-haspopup="true"
                  >
                    <BsThreeDotsVertical />
                  </div>
                  <Menu
                    id="simple-menu"
                    anchorEl={anchorEl}
                    keepMounted
                    open={Boolean(anchorEl)}
                    onClose={handleClose}
                  >
                    <MenuItem onClick={handleClose}>Edit</MenuItem>
                    <MenuItem onClick={handleClose}>Delete</MenuItem>
                  </Menu>
                </div>
                <div className="flex text-sm font-semibold gap-4 pb-2">
                  <div>{name}</div>
                  <div>{phone}</div>
                </div>
                <div className="text-sm w-2/3">
                  {address + ", " + city + ", " + state + " - "}{" "}
                  <span className="font-semibold">{pin}</span>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}
