import React from "react";

// ICONS
import { FaPowerOff } from "react-icons/fa";
import { BsFillPersonFill, BsFillFolderFill, BsPower } from "react-icons/bs";
import { HiChevronRight } from "react-icons/hi";
import { AiFillEdit } from "react-icons/ai";

import { useHistory } from "react-router-dom";
//REDUX
import { useSelector, useDispatch } from "react-redux";
import UserMenu from "../components/userMenu";
import { Edit } from "@material-ui/icons";
import Button from "../components/button";

export default function Welcome() {
  const state = useSelector((state) => state.AuthProvider);
  const [showEdit, setShowEdit] = React.useState(false);
  //   const dispatch = useDispatch();

  return (
    <div
      className="py-4 px-24"
      // style={{ backgroundImage: `url(${login})` }}
    >
      <div className="flex flex-row gap-4">
        <UserMenu />
        <div className="flex-1 bg-white shadow-lg rounded py-2 px-4 text-gray-600">
          <div className="flex justify-between">
            <div className="underline text-lg">Personal Information</div>
            <div className="flex">
              <div className="my-auto" className="cursor-pointer">
                <Edit fontSize={"small"} />
              </div>
            </div>
          </div>
          <div>
            <div>Photo</div>
            <div className="relative">
              <img
                src="https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/profile-pic-male_4811a1.svg"
                alt="dp"
                className="shadow-lg w-36 h-36 rounded-full"
                onMouseOver={() => setShowEdit(true)}
                onMouseOut={() => setShowEdit(false)}
              />
              {showEdit && (
                <div className="w-36 h-36 absolute bg-black opacity-50 top-0 left-0 rounded-full">
                  <div className="opacity-100 pt-auto">
                    <Edit fontSize={"small"} />
                    Edit
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
