import React from "react";

//REDUX
import { useSelector } from "react-redux";

import UserMenu from "../../components/userMenu";
import Clock from "../../assets/images/wall.jpeg";
import * as AppUtils from "../../helpers/constant";
import { FaStar } from "react-icons/fa";

const data = [
  {
    id: 1,
    image: Clock,
    name: "Titan Analog 30.5 cm x 30.5 cm",
    color: "Gray",
    seller: "RetailNet",
    price: 1299,
    dod: "May 19",
    isDelivery: true,
  },
  {
    id: 2,
    image: Clock,
    name: "Titan Analog 30.5 cm x 30.5 cm",
    color: "Gray",
    seller: "RetailNet",
    price: 1299,
    dod: "May 19",
    isDelivery: true,
  },
];

export default function Order() {
  const state = useSelector((state) => state.AuthProvider);

  return (
    <div
      className="py-4 px-24"
      // style={{ backgroundImage: `url(${login})` }}
    >
      <div className="flex flex-row gap-4">
        <UserMenu active={"orders"} />
        <div className="flex-1 bg-white shadow-lg rounded py-2 px-4 text-gray-600">
          <div className="flex justify-between gap-4 w-1/2 py-3">
            <div className="font-semibold text-lg">Orders</div>
          </div>

          {data.map((item) => {
            return (
              <div
                className="rounded border flex p-4 hover:shadow-lg mb-3"
                key={item.id}
              >
                <div className="flex-1 h-full flex justify-between gap-4">
                  <div className="w-24 ">
                    <div
                      className="w-20 h-20 bg-center bg-contain"
                      style={{ backgroundImage: `url(${Clock})` }}
                    ></div>
                  </div>
                  <div className="flex-1 ">
                    <div className="font-medium pb-2">
                      Titan Analog 30.5 cm x 30.5 cm
                    </div>
                    <div className="text-xs">Color: Gray</div>
                    <div className="text-xs">Seller: RetailNet</div>
                  </div>
                </div>
                <div className="flex-1 h-full flex justify-between gap-4">
                  <div className="w-24 ">{AppUtils.CURRENCY} 1299</div>
                  <div className="flex-1 ">
                    {item.isDelivery ? (
                      <div className="font-semibold pb-2 flex gap-1 ">
                        <div className="my-auto">
                          <div className="w-2 h-2 bg-green-600 rounded-full"></div>
                        </div>
                        <div>
                          <div>Delivered son May 19</div>
                        </div>
                      </div>
                    ) : (
                      <div className="font-semibold pb-2 flex gap-1 ">
                        <div className="my-auto">
                          <div className="w-2 h-2 bg-green-600 rounded-full"></div>
                        </div>
                        <div>
                          <div>Delivered on May 19</div>
                        </div>
                      </div>
                    )}
                    <div className="text-xs font-normal pb-2">
                      Your item has been delivered
                    </div>
                    <div className=" text-dodger-blue-500 font-semibold flex gap-1">
                      <div className="my-auto">
                        <FaStar />
                      </div>
                      <div>Rate and Review Product</div>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}
