import { CURRENCY } from "../helpers/constant";
// import ReactTooltip from "react-tooltip";
import { Link } from "react-router-dom";
// IMPORTING THEMES
import {
  ItemCardBase2,
  ItemCardImage2,
  ItemCardContent2,
  ItemCardPrice,
  ItemCardHead,
  ItemCardStars,
  ItemCardRatings,
} from "../utils/theme";

//StarRatings
import StarRatings from "react-star-ratings";

const ItemCard2 = ({
  image,
  title,
  ratings,
  price,
  numberOfRatings,
  onClick,
}) => {
  //   let desc = item.description;
  //   let length = JSON.stringify(desc).length;
  return (
    <Link to="/product">
      <div className={ItemCardBase2} onClick={onClick}>
        <div
          className={ItemCardImage2}
          style={{ backgroundImage: `url(${image})` }}
        ></div>
        <div className={ItemCardContent2}>
          <div className={ItemCardHead}>{title}</div>
          <div className={ItemCardPrice}>
            {CURRENCY} {Number(price).toFixed(2)}{" "}
          </div>
          <div className={ItemCardStars}>
            <StarRatings
              rating={ratings}
              starRatedColor="orange"
              // changeRating={this.changeRating}
              starDimension="20px"
              starSpacing="2px"
              numberOfStars={5}
              name="rating"
            />
            <span className={ItemCardRatings}>({numberOfRatings} Reviews)</span>
          </div>
        </div>
        {/*       
      <div
        className={ItemCardImage2}
