import React from "react";

import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";

import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import { makeStyles } from "@material-ui/core/styles";

//REDUX
import { useSelector, useDispatch } from "react-redux";
import UserMenu from "../../components/userMenu";
import Button from "../../components/button";
import Input from "../../components/input";
import { BsThreeDotsVertical } from "react-icons/bs";

const address = [
  {
    id: "1",
    type: "HOME",
    name: "Vaibhav Sinha",
    phone: "6205630243",
    pin: "828104",
    address:
      "S/o Mr Dilip Kumar Sinha Shyam Nagar, Bhuli Dhanbad, Near Aadarsh Vidya Mandir School",
    city: "Dhanbad",
    state: "Jharkhand",
  },
  {
    id: "2",
    type: "office",
    name: "Vaibhav Sinha",
    phone: "6205630243",
    pin: "828104",
    address:
      "S/o Mr Dilip Kumar Sinha Shyam Nagar, Bhuli Dhanbad, Near Aadarsh Vidya Mandir School",
    city: "Dhanbad",
    state: "Jharkhand",
  },
  {
    id: "3",
    type: "office",
    name: "Vaibhav Sinha",
    phone: "6205630243",
    pin: "828104",
    address:
      "S/o Mr Dilip Kumar Sinha Shyam Nagar, Bhuli Dhanbad, Near Aadarsh Vidya Mandir School",
    city: "Dhanbad",
    state: "Jharkhand",
  },
  {
    id: "4",
    type: "office",
    name: "Vaibhav Sinha",
    phone: "6205630243",
    pin: "828104",
    address:
      "S/o Mr Dilip Kumar Sinha Shyam Nagar, Bhuli Dhanbad, Near Aadarsh Vidya Mandir School",
    city: "Dhanbad",
    state: "Jharkhand",
  },
  {
    id: "5",
    type: "office",
    name: "Vaibhav Sinha",
    phone: "6205630243",
    pin: "828104",
    address:
      "S/o Mr Dilip Kumar Sinha Shyam Nagar, Bhuli Dhanbad, Near Aadarsh Vidya Mandir School",
    city: "Dhanbad",
    state: "Jharkhand",
  },
];

const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1, 1, 0, 0),
  },
  icon: {
    borderRadius: "50%",
    width: 16,
    height: 16,
    boxShadow:
      "inset 0 0 0 1px rgba(16,22,26,.2), inset 0 -1px 0 rgba(16,22,26,.1)",
    backgroundColor: "#f5f8fa",
    backgroundImage:
      "linear-gradient(180deg,hsla(0,0%,100%,.8),hsla(0,0%,100%,0))",
    "$root.Mui-focusVisible &": {
      outline: "2px auto rgba(19,124,189,.6)",
      outlineOffset: 2,
    },
    "input:hover ~ &": {
      backgroundColor: "#ebf1f5",
    },
    "input:disabled ~ &": {
      boxShadow: "none",
      background: "rgba(206,217,224,.5)",
    },
  },
  checkedIcon: {
    backgroundColor: "#297162",
    width: "16px",
    height: "16px",
    borderRadius: "100%",
    "input:hover ~ &": {
      backgroundColor: "#1C4B42",
    },
  },
}));

function StyledRadio(props) {
  const classes = useStyles();

  return (
    <Radio
      className={classes.root}
      disableRipple
      color="default"
      checkedIcon={<span className={(classes.icon, classes.checkedIcon)} />}
      icon={<span className={classes.icon} />}
      {...props}
    />
  );
}

export default function Address() {
  const classes = useStyles();
  const state = useSelector((state) => state.AuthProvider);
  const [value, setValue] = React.useState("");
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleRadioChange = (event) => {
    setValue(event.target.value);
  };

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <div
      className="py-4 px-24"
      // style={{ backgroundImage: `url(${login})` }}
    >
      <div className="flex flex-row gap-4">
        <UserMenu active={"address"} />
        <div className="flex-1 bg-white shadow-lg rounded py-2 px-4 text-gray-600">
          <div className="flex justify-between gap-4 w-1/2 py-3">
            <div className="underline text-lg">Manage Address</div>
          </div>
          <div className="mb-4">
            <Button
              size={"small"}
              className="w-full my-auto text-xs"
              variant={"primary"}
            >
              Add A New Address
            </Button>
            <div
              className="h-auto w-full my-3 p-3"
              style={{ background: "rgba(200, 235, 228, 0.3)" }}
            >
              <span className="text-sm font-semibold uppercase text-gray-500">
                Add a new address
              </span>
              <div className="my-2">
                <div className="flex gap-5">
                  <div className="mb-5 flex-1">
                    {/* Name */}
                    <Input
                      placeholder="Name"
                      className={`mb-10px `}
